import React from 'react';
import { motion } from 'framer-motion';

const BusinessBackground = ({ variant = 'home' }) => {
  const patterns = {
    home: (
      <>
        <defs>
          <pattern id="grid" x="0" y="0" width="40" height="40" patternUnits="userSpaceOnUse">
            <path d="M 40 0 L 0 0 0 40" fill="none" stroke="currentColor" strokeWidth="1" />
          </pattern>
          <pattern id="circles" x="0" y="0" width="60" height="60" patternUnits="userSpaceOnUse">
            <circle cx="30" cy="30" r="15" fill="none" stroke="currentColor" strokeWidth="1" />
          </pattern>
        </defs>
        <rect width="100%" height="100%" fill="url(#grid)" className="text-indigo-100 opacity-20" />
        <rect width="100%" height="100%" fill="url(#circles)" className="text-indigo-200 opacity-20" />
        <motion.g
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 1 }}
        >
          <path d="M0,0 L100,0 L50,100 Z" fill="currentColor" className="text-indigo-50 opacity-10" />
          <path d="M100,100 L0,100 L50,0 Z" fill="currentColor" className="text-purple-50 opacity-10" />
        </motion.g>
      </>
    ),
    dashboard: (
      <>
        <defs>
          <pattern id="dots" x="0" y="0" width="20" height="20" patternUnits="userSpaceOnUse">
            <circle cx="2" cy="2" r="1" fill="currentColor" />
          </pattern>
          <pattern id="waves" x="0" y="0" width="100" height="20" patternUnits="userSpaceOnUse">
            <path d="M0,10 Q25,0 50,10 T100,10" fill="none" stroke="currentColor" strokeWidth="1" />
          </pattern>
        </defs>
        <rect width="100%" height="100%" fill="url(#dots)" className="text-indigo-100 opacity-20" />
        <rect width="100%" height="100%" fill="url(#waves)" className="text-purple-100 opacity-20" />
        <motion.g
          initial={{ opacity: 0, scale: 0.8 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 1 }}
        >
          <circle cx="80%" cy="20%" r="100" fill="currentColor" className="text-indigo-50 opacity-20" />
          <circle cx="20%" cy="80%" r="100" fill="currentColor" className="text-purple-50 opacity-20" />
        </motion.g>
      </>
    )
  };

  return (
    <div className="absolute inset-0 overflow-hidden">
      <div className="absolute inset-0 bg-gradient-to-br from-white to-indigo-50">
        <svg
          className="w-full h-full"
          xmlns="http://www.w3.org/2000/svg"
          preserveAspectRatio="xMidYMid slice"
        >
          {patterns[variant]}
        </svg>
      </div>
      <motion.div
        className="absolute inset-0 bg-gradient-to-tr from-transparent via-white to-transparent opacity-40"
        initial={{ opacity: 0 }}
        animate={{ opacity: 0.4 }}
        transition={{ duration: 2, repeat: Infinity, repeatType: 'reverse' }}
      />
    </div>
  );
};

export default BusinessBackground;
